import React, { useState } from 'react';
import { Navbar, Container, Nav, Form, FormControl, Button } from 'react-bootstrap';
import '../assets/css/navbar.css';
import { Link, useNavigate } from 'react-router-dom';

const CustomNavbar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchTerm.trim())}`);
    } else {
      navigate('/allbooks');
    }
  };

  return (
    <Navbar expand="lg" className="sticky-top navbar-custom">
      <Container fluid>
        <Navbar.Brand as={Link} to="/" className="navbar-brand-custom">Logo</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Form className="d-flex ms-auto" onSubmit={handleSearch}>
            <FormControl 
              type="search" 
              placeholder="Search" 
              className="mr-2 search-input-custom" 
              aria-label="Search" 
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Button variant="primary" type="submit" className="search-button-custom">
              <i className="fas fa-search"></i> Search
            </Button>
          </Form>
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/addbook" className="navbar-link-custom">Add Book</Nav.Link>
            <Nav.Link as={Link} to="/login" className="navbar-link-custom">Login</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
