import React from 'react';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import BooksData from '../books.json';

const OneBook = () => {
  const { category, bookId } = useParams();
  const book = BooksData[category].find(b => b.id === parseInt(bookId));

  const [isImageLarge, setIsImageLarge] = useState(false);

  const handleImageClick = () => {
    setIsImageLarge(!isImageLarge);};

  if (!book) {
    return <p>Book not found</p>;
  }

  return (
    <Container className="py-5">
    <Row>
        <Col md={4}>
        <Card>
            <Card.Img
                variant="top"
                src={book.cover}
                alt={book.title}
                style={{
                height: '500px', // Ensure the image height is fixed
                objectFit: 'contain', // Use 'contain' to fit the image inside the container without stretching
                maxHeight: '100%', // Ensure the image does not exceed the maximum height of the container
                width: '100%' // Ensure the image takes the full width of the container
                }}
            />
        </Card>
        <Button variant="danger" className="my-3" style={{ width: '100%' }}>Delete Book</Button>
        </Col>
        <Col md={8}>
        <h1 className="mb-3" style={{ fontWeight: 'bold' }}>{book.title}</h1>
        <h4 className="mb-3">{book.author}</h4>
        <p className="mb-3"><strong>Description:</strong></p>
        <p className="mb-4">{book.description}</p>
        </Col>
    </Row>
    </Container>
  );
};

export default OneBook;
