import React from 'react';
import { useLocation } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import BookCard from './BookCard';
import BooksData from '../books.json';

const SearchResults = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const query = searchParams.get('q');

  const allBooks = Object.values(BooksData).flat();
  const filteredBooks = allBooks.filter(book => 
    book.title.toLowerCase().includes(query.toLowerCase()) ||
    book.author.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <Container fluid className="py-5">
      <h1 className="text-center mb-5" style={{ color: '#333', fontWeight: 'bold' }}>
        Search Results for "{query}"
      </h1>
      <Row xs={2} sm={3} md={4} lg={6} className="g-4 mx-4">
        {filteredBooks.length > 0 ? (
          filteredBooks.map((book) => (
            <Col key={book.id}>
              <BookCard book={book} />
            </Col>
          ))
        ) : (
          <Col>
            <p className="text-center" style={{ color: '#666' }}>No books found matching your search.</p>
          </Col>
        )}
      </Row>
    </Container>
  );
};

export default SearchResults;
