import React from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import BookCard from './BookCard';
import BooksData from '../books.json';

const CategoryBooks = () => {
  const { categoryName } = useParams(); // Retrieve the category name from the URL
  const books = BooksData[categoryName] || []; // Get books for the category

  return (
    <Container fluid className="py-5">
      <h1 className="text-center mb-5" style={{ color: '#333', fontWeight: 'bold' }}>
        Books in {categoryName.charAt(0).toUpperCase() + categoryName.slice(1)}
      </h1>
      <Row xs={2} sm={3} md={4} lg={6} className="g-4 mx-4">
          books.map((book) => (
            <Col key={book.id}>
              <BookCard book={book} />
            </Col>
          ))
      </Row>
    </Container>
  );
};

export default CategoryBooks;
