import React, { useState } from 'react';
import './MonComposant.css'; // Importer le fichier CSS pour styliser le composant


function MonComposant() {
  const [showText, setShowText] = useState(true);

  const handleDelete = () => {
    setShowText(false);
  };

  return (
    <div className="mon-composant">
      {showText && (
        <>
          <div className="image-container">
            <img src={myImg} alt="Description de l'image" />
          </div>
          <div className="text-container">
            <h1>L'homme qui voulait être heureux</h1>
            <h4>Laurent Gounelle</h4>
            <p>
              Imaginez... Vous êtes en vacances à Bali et peu de temps avant votre retour, vous consultez un vieux guérisseur. Sans raison particulière, juste parce que sa grande réputation vous a donné envie de le rencontrer, au cas où... Son diagnostic est formel : vous êtes en bonne santé, mais vous n'êtes pas... heureux. Porteur d'une sagesse infinie, ce vieil homme semble vous connaître mieux que vous-même.
            </p>

            <button onClick={handleDelete}>DELETE</button>
            
          </div>
        </>
      )}
    </div>
  );
}



export default MonComposant;
