import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

// Sample book data (replace this with your actual data fetching logic)
const books = [
  { id: 1, title: "Into Thin Air", author: "Harper Lee", cover: "https://example.com/mockingbird-cover.jpg", description: "A classic of modern American literature." },
  { id: 2, title: "Moby-Dick", author: "George Orwell", cover: "https://example.com/1984-cover.jpg", description: "A dystopian social science fiction novel." },
  { id: 3, title: "Pride and Prejudice", author: "Jane Austen", cover: "https://example.com/pride-prejudice-cover.jpg", description: "A romantic novel of manners." },
  { id: 4, title: "The Great Gatsby", author: "F. Scott Fitzgerald", cover: "https://example.com/great-gatsby-cover.jpg", description: "A tragic story of the American dream." },
  // Add more books as needed
];

const BookCard = ({ book }) => (
  <Card className="h-100 shadow-sm">
    <Card.Img variant="top" src={book.cover} alt={book.title} style={{ height: '200px', objectFit: 'cover' }} />
    <Card.Body className="d-flex flex-column">
      <Card.Title>{book.title}</Card.Title>
      <Card.Subtitle className="mb-2 text-muted">{book.author}</Card.Subtitle>
      <Card.Text>{book.description}</Card.Text>
      <Button variant="primary" className="mt-auto">View Details</Button>
    </Card.Body>
  </Card>
);

const BooksPage = () => {
  return (
    <Container fluid className="py-5" style={{ backgroundColor: '#f8f9fa' }}>
      <h1 className="text-center mb-5" style={{ color: '#333', fontWeight: 'bold' }}>Our Book Collection</h1>
      <Row xs={1} sm={2} md={3} lg={4} className="g-4">
        {books.map((book) => (
          <Col key={book.id}>
            <BookCard book={book} />
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default BooksPage;
