import React, { useState } from 'react';
import { Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const BookCard = ({ category, book }) => {
  const [isImageLarge, setIsImageLarge] = useState(false);

  const handleImageClick = () => {
    setIsImageLarge(!isImageLarge);
  };

  return (
    <Card className="mb-4 shadow-sm">
      <div 
        onClick={handleImageClick} 
        style={{
          cursor: 'pointer', 
          overflow: 'hidden', 
          transition: 'transform 0.3s ease',
          transform: isImageLarge ? 'scale(1.2)' : 'scale(1)'
        }}
      >
        <Card.Img 
          variant="top" 
          src={book.cover} 
          alt={book.title} 
          style={{ 
            height: '350px', 
            objectFit: 'contain', 
            width: '100%', 
            transition: 'transform 0.3s ease' 
          }} 
        />
      </div>
      <Card.Body>
        <Card.Title>{book.title}</Card.Title>
        <Card.Text>Author: {book.author}</Card.Text>
        <Link to={`/allbooks/${category}/${book.id}`} className="btn btn-primary">
          View Details
        </Link>
      </Card.Body>
    </Card>
  );
};

export default BookCard;
