import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

// Sample book data (replace this with your actual data fetching logic)
const books = {
    "Adventure": [
        {
            "id": 1,
            "title": "Into Thin Air",
            "author": "Jon Krakauer",
            "cover": "https://paulinaontheroad.com/wp-content/uploads/2023/12/512t9z6JLLL._SL500_.jpg.webp"
        },
        {
            "id": 2,
            "title": "Moby-Dick",
            "author": "Herman Melville",
            "cover": "https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcSE9EdW4IsRCSJYyJxefVVsEaZb7CwX4HXVg4Rc28uv64vLhP26"
        },
        {
            "id": 3,
            "title": "Into Thin Air",
            "author": "Jon Krakauer",
            "cover": "https://paulinaontheroad.com/wp-content/uploads/2023/12/512t9z6JLLL._SL500_.jpg.webp"
        },
        {
            "id": 4,
            "title": "The Hobbit",
            "author": "John Ronald Reuel Tolkien",
            "cover": "https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcRRxdD7tWquroN4RfhCCpDQeOD7q4DnTHqUpp8gdAW9hzhoSftn"
        },
        {
            "id": 5,
            "title": "Harry Potter and the Philosopher's Stone",
            "author": "J. K. Rowling",
            "cover": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQtFKRzqIPBTYkse7NKIPTjw0ggQB02c636StmC1mONyQwec4x1"
        },
        {
            "id": 6,
            "title": "Treasure Island",
            "author": "Robert Louis Stevenson",
            "cover": "https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcR_AqOfDPs9ufnpFhFmLzdHs-vGLnKta9W_L28oV-g0epU0MkYI"
        }
    ],
    "Romance": [
        {
            "id": 1,
            "title": "Pride and Prejudice",
            "author": "Jane Austen",
            "cover": "https://images-na.ssl-images-amazon.com/images/I/81UWUzi7I2L.jpg"
        },
        {
            "id": 2,
            "title": "The Notebook",
            "author": "Nicholas Sparks",
            "cover": "https://images-na.ssl-images-amazon.com/images/I/51oW8KPYo7L.jpg"
        },
        {
            "id": 3,
            "title": "Outlander",
            "author": "Diana Gabaldon",
            "cover": "https://images-na.ssl-images-amazon.com/images/I/91vxFy0lE0L.jpg"
        },
        {
            "id": 4,
            "title": "Me Before You",
            "author": "Jojo Moyes",
            "cover": "https://images-na.ssl-images-amazon.com/images/I/91Q5dCjc2KL.jpg"
        },
        {
            "id": 5,
            "title": "Twilight",
            "author": "Stephenie Meyer",
            "cover": "https://images-na.ssl-images-amazon.com/images/I/91P6rHh0oML.jpg"
        },
        {
            "id": 6,
            "title": "The Time Traveler's Wife",
            "author": "Audrey Niffenegger",
            "cover": "https://images-na.ssl-images-amazon.com/images/I/91o2nMsdMLL.jpg"
        }
    ]


    
}

const BookCard = ({ book }) => (
  <Card className="h-100 shadow-sm">
    <Card.Img variant="top" src={book.cover} alt={book.title} style={{ height: '200px', objectFit: 'cover' }} />
    <Card.Body className="d-flex flex-column">
      <Card.Title>{book.title}</Card.Title>
      <Card.Subtitle className="mb-2 text-muted">{book.author}</Card.Subtitle>
      <Card.Text>{book.description}</Card.Text>
      <Button variant="primary" className="mt-auto">View Details</Button>
    </Card.Body>
  </Card>
);

const BooksPage = () => {
  return (
    <Container fluid className="py-5" style={{ backgroundColor: '#f8f9fa' }}>
      <h1 className="text-center mb-5" style={{ color: '#333', fontWeight: 'bold' }}>Our Book Collection</h1>
      <Row xs={1} sm={2} md={3} lg={4} className="g-4">
        {books.map((book) => (
          <Col key={book.id}>
            <BookCard book={book} />
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default BooksPage;
